import{openDB}from"idb";const initdb=async()=>openDB("jate",1,{upgrade(t){t.objectStoreNames.contains("jate")?console.log("jate database already exists"):(t.createObjectStore("jate",{keyPath:"id",autoIncrement:!0}),console.log("jate database created"))}});export const putDb=async t=>{console.log("Post to the DB");const e=(await openDB("jate",1)).transaction("jate","readwrite").objectStore("jate").put({id:1,content:t}),o=await e;console.log("Result: ",o)};export const getDb=async()=>{console.log("Get from the DB");const t=(await openDB("jate",1)).transaction("jate","readonly").objectStore("jate").getAll(),e=await t;return console.log("Result: ",e),e};initdb();